                                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                                      />


                                      {filteredName.floors.map((floor, index) => (
                                      <LayersControl.Overlay checked name={floor.description}>
                                      <LayerGroup>
                                        {floor.boundaries.map((vertex,index) => (

                                              <Marker position={vertex} >
                                              <Popup>
                                              delete<br />
                                              </Popup>
                                              </Marker>
