const DisplayEntries = ({ names, regVal }) => {
    const regExp = new RegExp(regVal, "i")
    const filteredArray = names.filter((entry) => regExp.test(entry.name))
    const namesArray = filteredArray.map((entry) => <DisplayEntry key={entry.refnum} name={entry.name} latitude={entry.lat} longitude={entry.lng} id={entry.refnum} />)

    return(
    <div>
        {namesArray}
    </div>
    )
}

export default DisplayEntries
