import React, {useState, useEffect} from "react"
import service from "./services.js"
import {Row, Col, Table} from 'reactstrap';


const DisplayEntry = ({ lat, lng, name, id }) => {
    const [ newName, setName ] = useState(name)
    const [ newLat, setLat] = useState(lat)
    const [ newLng, setLng] = useState(lng)
  //  const [ newNumber, setNumber ] = useState(number)
    const [ buttonVal, setButtonVal ] = useState("")
    const [columns] = useState([
        {
            title: "Name",
            name: "name",
        },
        {
            title: "Lat",
            name: "lat",
            sortable: true,
        },
        {
            title: "Lng",
            name: "lng",
            sortable: true,
        },
    ]);
    const [rows, setRows] = useState([
        {newName},
        {newLat},
        {newLng}

    ]);
    const [editingCells, setEditingCells] = useState([]);

    const deleteHandler = (id) =>{
        const handler = () =>{
            if(window.confirm(`Do you reall want to delete ${name}\'s number?`)){
                service.deletion(id)
                .then(response => {

                    setName("")
                    setLat("")
                    setLng("")

                    setButtonVal("")
                })
            }
        }
        return handler
    }

    useEffect(()=>{
        setButtonVal(<button onClick={deleteHandler(id)}>delete</button>)
    },
    [])

    return(
        <div>
            {/* <Table striped bordered hover>
            <thead>
        <tc>
      <th>Name</th>
      <th>Lat</th>
      <th>Lng</th>
     </tc>
  </thead>
  <tbody>
    <tr>
      <td>{newName} </td>
      <td>{newLat}  </td>
      <td> {newLng} </td>
      <td> {buttonVal} </td>
    </tr>
    </tbody>




            </Table> */}
             <Row>
            <Col sm="12">
                <Card className="iq-card">
                    {/* <div className="iq-card-header d-flex justify-content-between">
                        <CardTitle className="iq-header-title">
                            <h4 className="card-title">Editable Table</h4>
                        </CardTitle>
                    </div> */}
                    <CardBody className="iq-card-body">
                        <Grid
                            rows={rows}
                            columns={columns}
                            getRowId={getRowId}
                        >
                            <EditingState
                                onCommitChanges={commitChanges}
                                editingCells={editingCells}
                                onEditingCellsChange={setEditingCells}
                                addedRows={[]}
                                onAddedRowsChange={addEmptyRow}
                            />
                            <Table cellComponent={FocusableCell} />
                            <TableHeaderRow />
                            <TableInlineCellEditing selectTextOnEditStart />
                            <TableEditColumn>
                                {buttonVal}
                                </TableEditColumn>
                        </Grid>
                    </CardBody>
                </Card>
            </Col>
        </Row>
        </div>
    )
}

const DisplayEntries = ({ names, regVal }) => {
    const regExp = new RegExp(regVal, "i")
    const filteredArray = names.filter((entry) => regExp.test(entry.name))
    const namesArray = filteredArray.map((entry) => <DisplayEntry key={entry.id} name={entry.name} lat={entry.lat} lng={entry.lng} id={entry.id} />)

    return(
    <div>
        {namesArray}
    </div>
    )
}

export default DisplayEntries
