                .then(response => {

                    setName("")
                    setLat("")
                    setLng("")

                    setButtonVal("")
                })
            }
        }
        return handler
    }

    useEffect(()=>{
        setButtonVal(<button onClick={deleteHandler(id)}>delete</button>)
    },
    [])

