import React, { useEffect, useState } from "react";



import DisplayEntries from "./DisplayEntries"
import service from "./services.js";

import { Polygon, Popup, Rectangle, Marker, TileLayer } from "react-leaflet";

import {MapContainer } from "react-leaflet";
import { Card, CardBody, Col, Row } from "reactstrap";
import {Nav,NavItem,NavLink,TabContent,TabPane} from 'reactstrap';
import Tabs from "./Tabs";
import PropTypes from "prop-types";
import DashBoard from "./Dashboard";
//import icon from "./icon_building.png";
import { Icon } from "leaflet";

const Map1  = () => {



    const [markers, setMarkers] = useState([])
      const [ newLat, setNewLat ] = useState('')
      const [ newLng, setNewLng ] = useState('')
      const [ newName, setNewName ] = useState('')
      const [newFilter, setNewFilter] = useState('')


      useEffect(()=>{
        service
        .getAll()
        .then(latlng =>{
          console.log("returning", latlng)
          setMarkers(latlng)
        })
      },[])







      const kids = [
        [60.23296, 24.83034],
        [60.23299, 24.83039],
        [60.23302, 24.83031],
        [60.23299, 24.83026]

      ]
      const br = [
        [60.23299, 24.83026],
        [60.23301, 24.83029],
        [60.23303, 24.83024],
        [60.23301, 24.83022]
      ]
      const tv = [
        [60.23301, 24.83030],
        [60.23304, 24.83035],
        [60.23306, 24.83030],
        [60.23303, 24.83025]
      ]
      const wc = [
        [60.23303, 24.83040],
        [60.23304, 24.83042],
        [60.23305, 24.83039],
        [60.23303, 24.83038]
      ]




      const Corridar = [
        [60.23299, 24.83040],
        [60.23301, 24.83043],
        [60.23303, 24.83037],
        [60.23302, 24.83034],
      ]
      const Kitchen = [
        [60.23304, 24.83035],
        [60.23306, 24.83038],
        [60.23308, 24.83033],
        [60.23306, 24.83030]
      ]
  const changeHandler = (event) => setNewLat(event.target.value)
  const changeHandler1 = (event) => setNewLng(event.target.value)
  const changeHandler2= (event) => setNewName(event.target.value)
  const changeHandlerFilter = (event) => setNewFilter(event.target.value)



        const position = [60.21749913, 24.938379];
        const position1 = [60.21749913, 24.806496774]
        console.log(position)

      const submitHandler = (event) => {
        event.preventDefault()
       service
        .getAll()
        .then(latlng =>{
          const allMarkers = [...latlng]

          const newMarker = {
            id:markers.length + 1,
            lat:parseFloat(newLat),
            lng:parseFloat(newLng),
            name:newName
          }
           service
            .update(newMarker)
            .then(newEntry =>{
                setNewLat("")
                setNewLng("")
                setNewName("")
              setMarkers(latlng.concat(newEntry))
            })

        })
    }
    function renderPopup (item){

      return (

        <Popup

        >

          <Link  to={item.link} >{item.description}</Link>
        </Popup>

      );
    }

    // function renderPopup (index){
    //   return (

    //     <Popup
    //       tipSize={5}
    //       anchor="bottom-right"
    //       longitude={markers[index].lng}
    //       latitude={markers[index].lat}
    //     >
    //       <p>
    //          <button type="submit" onClick={submitHandler}>{markers[index].name}</button> 
    //         <br />



    //       </p>
    //     </Popup>

    //   );
    // }
    const handleClick = (e) => {
      e.latlng()
    }

    const building = new Icon({
      iconUrl: "./icon_building.svg",
      iconSize: [25, 25]
    });
  return (
//       <Row className="iq-example-row">
<Container>

<Row className="row">
<Col className="col-4">
<Table hover className="table" items={markers}>
   <thead>
      <tr>
         <th scope="col">#</th>
         <th scope="col">Name</th>
         <th scope="col">Address</th>
         <th scope="col"></th>

      </tr>
   </thead>
   <tbody>
                                {
                                    markers.map((item, index) => (

                                        <tr key={index}>
                                            <td >{item.refnum} </td>
                                           <td> <Link   to={item.link} className="nav-link font-weight-bold ">{item.description} </Link></td>
                                            <td >{item.street}   {item.Apartment} {item.doornum} {item.region} {item.country}</td>
                                            <Link>Delete</Link>
                                        </tr>
                                    ))
                                }


   </tbody>
</Table>


</Col>
<Col className="col-8">
<MapContainer
                                            style={ { height: "500px", width: "100%"}}

                                            center={[60.21679719545689, 24.810291821854594]} zoom={12} maxZoom={100}

                                        >

                                                <TileLayer
                                            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                                            attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                                            />

                                {
                                    markers.map((item, index) => (

                                        <Marker   
                                                position={[item.latitude,item.longitude]}
                                                onMouseOver={(e) => {
                                                    e.target.openPopup();
                                                  }}
                                                  onMouseOut={(e) => {
                                                    e.target.closePopup();
                                                  }}
                                                >
                                                          {renderPopup(item) }
                                                </Marker>
                                    ))
                                }




                                     </MapContainer>
</Col>
</Row>
</Container>


  );
}




export default Map1
